import React, { useEffect, useState } from "react";
import "./Dropdown.css";
import options from '../options/Options'




const Dropdown = () => {
    /* mini version = redux, fn comopnent feature */
    const [processed_opts, setProcessed_opts] = useState()
    const [expanded, setExpanded] = useState(false);
    const [selections, setSelections] = useState([]);


    useEffect(() => {
        /* map || reduce fn to add a checked field for multiple selections */
        let tmp_data = options.map(item => {
            let tmp_obj = item
            tmp_obj.isChecked = false
            return tmp_obj
        })
        setProcessed_opts(tmp_data)
    }, [])



    const toggleExpanded = () => {
        if (!expanded) {
            setExpanded(true);
        } else {
            setExpanded(false);
        }
    };

    const handleChange = event => {
        if (event.target.checked) {
            return setSelections([...selections, event.target.name]);
        }
        const filtered = selections.filter(name => name !== event.target.name);
        return setSelections(filtered);
    };


    return (

        <main className="main__div">
            <div onClick={toggleExpanded}>
                <div
                    className={`font - semibold cursor - pointer ${expanded ? "up-arrow" : "down-arrow"
                        } `}
                >
                    {selections.length
                        ? selections.map((name, i) => (
                            <span key={i}>
                                {i ? ", " : null}
                                {name}
                            </span>
                        ))
                        : "None selected"}
                </div>
            </div>
            {expanded && (
                <div className="border-gray-200 border border-solid">
                    {processed_opts && processed_opts.map((opt, idx) => (
                        <label
                            key={idx}
                            htmlFor="one" className="block" key={idx}>
                            <input
                                type="checkbox"
                                name={opt.value}
                                value={opt.value}
                                onChange={handleChange}
                                className="m-3 cursor-pointer"
                            />
                            {opt.value}
                        </label>
                    ))}
                </div>
            )}
        </main>
    );
};

const DropDownContainer = () => {
    return (
        <div className="w-full h-screen flex justify-center items-center">
            <div className="w-1/2 h-16  text-black-100 flex items-center justify-center text-xl">
                <Dropdown />
            </div>
        </div>
    );
};

export default DropDownContainer;
