import { computeHeadingLevel } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./Dropdown.css";

const Dropdown = ({ ...options }) => {
    console.log(options.options.options, '***')
    /* mini version = redux, fn comopnent feature */
    const [processed_opts, setProcessed_opts] = useState();
    const [expanded, setExpanded] = useState(false);
    const [selections, setSelections] = useState([]);




    useEffect(() => {
        /* map || reduce fn to add a checked field for multiple selections */
        let tmp_data = options.options.options.map((item) => {
            let tmp_obj = item;
            tmp_obj.isChecked = false;
            return tmp_obj;
        });
        setProcessed_opts(tmp_data);
    }, []);

    const toggleExpanded = () => {
        if (!expanded) {
            setExpanded(true);
        } else {
            setExpanded(false);
        }
    };
    console.log(processed_opts)
    const handleChange = (e, idx) => {
        if (e.target.checked) {
            /* set myfilter field isChecked true */
            console.log(e.target.dataset.rowid)
            let tmpArr = [...processed_opts]
            tmpArr.isChecked = e.target.checked
            setProcessed_opts(tmpArr)



            return setSelections([...selections, e.target.name]);
        }
        const filtered = selections.filter((name) => name !== e.target.name);

        return setSelections(filtered);
    };

    return (
        <main className="main__div">
            <div className="searchbar__div" onClick={toggleExpanded}>
                <div
                    className={`font - semibold cursor - pointer ${expanded ? "up-arrow" : "down-arrow"
                        } `}
                >
                    {selections.length
                        ? selections.map((name, i) => (
                            <span key={i}>

                                {i ? ", " : null} {name}
                            </span>
                        ))
                        : "None selected"}
                </div>
            </div >
            {expanded && (
                <div className=" expanded__div">
                    {processed_opts &&
                        processed_opts.map((opt, idx) => (
                            <label className="block__label" key={idx}>
                                <input
                                    data-rowid={idx}
                                    type="checkbox"
                                    name={opt.value}
                                    value={opt.value}
                                    onChange={e => handleChange(e)}
                                    className="m-3 cursor-pointer"
                                />
                                {opt.value}
                            </label>
                        ))}
                </div>
            )}
        </main >
    );
};

const DropDownContainer = ({ ...options }) => {
    console.log(options, '$$$$$$')
    return (
        <div className="w-full h-screen flex justify-center items-center">
            <div className="w-1/2 h-16  text-black-100 flex items-center justify-center text-xl">
                <Dropdown options={options} />
            </div>
        </div>
    );
};

export default DropDownContainer;
