import { useEffect, useState } from 'react';
import options from '../options/Options'

import './Dropdown.css'





const Dropdown = () => {
    /* mini version = redux, fn comopnent feature */
    const [data, setData] = useState()

    /* 1. pre-process data from backend */
    /* dep. array = ComponentDidMount */
    useEffect(() => {
        /* map || reduce fn to add a checked field for multiple selections */
        let tmp_data = options.map(item => {
            let tmp_obj = item
            tmp_obj.isChecked = false
            return tmp_obj
        })
        setData(tmp_data)
    }, [])

    console.log(data)

    return (
        <main className="main__div">
            <div
                className="option__select"
            >
                {data && data.map((opt, idx) => {
                    return (
                        <label>
                            <input
                                type="checkbox"
                                key={idx}></input>
                        </label>
                    )
                })}
            </div>
        </main>
    );
};

export default Dropdown;
