import React, { useState } from "react";
import "./Dropdown.css";

const PLATFORMS = ["Instagram", "LinkedIn", "Twitter"];

const Dropdown = () => {
    const [expanded, setExpanded] = useState(false);
    const [selections, setSelections] = useState([]);

    const toggleExpanded = () => {
        if (!expanded) {
            setExpanded(true);
        } else {
            setExpanded(false);
        }
    };

    const handleChange = event => {
        if (event.target.checked) {
            return setSelections([...selections, event.target.name]);
        }
        const filtered = selections.filter(name => name !== event.target.name);
        return setSelections(filtered);
    };

    const handleSubmit = e => {
        e.preventDefault();
        console.log("Submitted! Values selected are", selections);
        toggleExpanded();
    };

    return (
        <form onSubmit={handleSubmit} className="w-full">
            <div>
                <div onClick={toggleExpanded}>
                    <h6>PLATFORMS </h6>
                    <div
                        className={`font - semibold cursor - pointer ${expanded ? "up-arrow" : "down-arrow"
                            } `}
                    >
                        {selections.length
                            ? selections.map((name, i) => (
                                <span key={i}>
                                    {i ? ", " : null}
                                    {name}
                                </span>
                            ))
                            : "None selected"}
                    </div>
                </div>
                {expanded && (
                    <div className="border-gray-200 border border-solid">
                        {PLATFORMS.map(platform => (
                            <label htmlFor="one" className="block" key={platform}>
                                <input
                                    type="checkbox"
                                    name={platform}
                                    value={platform}
                                    onChange={handleChange}
                                    className="m-3 cursor-pointer"
                                />
                                {platform}
                            </label>
                        ))}
                    </div>
                )}
            </div>
            <button type="submit">Submit</button>
        </form>
    );
};

const App = () => {
    return (
        <div className="w-full h-screen flex justify-center items-center">
            <div className="w-1/2 h-16  text-black-100 flex items-center justify-center text-xl">
                <Dropdown />
            </div>
        </div>
    );
};

export default App;
